func (r *ReportConfigResource) Update(ctx context.Context, req resource.UpdateRequest, resp *resource.UpdateResponse) {
	var data ReportConfigResourceModel
	resp.Diagnostics.Append(req.Plan.Get(ctx, &data)...)
	if resp.Diagnostics.HasError() {
		return
	}

	updateTimeout, diags := data.Timeouts.Update(ctx, inttimeouts.DefaultUpdate)
	resp.Diagnostics.Append(diags...)
	if resp.Diagnostics.HasError() {
		return
	}

	ctx, cancel := context.WithTimeout(ctx, updateTimeout)
	defer cancel()

	apiResource := &client.ReportConfig{
		Metadata: client.Metadata{
			Name:      data.Name.ValueString(),
			Namespace: data.Namespace.ValueString(),
		},
		Spec: make(map[string]interface{}),
	}

	if !data.Description.IsNull() {
		apiResource.Metadata.Description = data.Description.ValueString()
	}

	if !data.Labels.IsNull() {
		labels := make(map[string]string)
		resp.Diagnostics.Append(data.Labels.ElementsAs(ctx, &labels, false)...)
		if resp.Diagnostics.HasError() {
			return
		}
		apiResource.Metadata.Labels = labels
	}

	if !data.Annotations.IsNull() {
		annotations := make(map[string]string)
		resp.Diagnostics.Append(data.Annotations.ElementsAs(ctx, &annotations, false)...)
		if resp.Diagnostics.HasError() {
			return
		}
		apiResource.Metadata.Annotations = annotations
	}

	// Marshal spec fields from Terraform state to API struct
	if data.ReportRecipients != nil {
		report_recipientsMap := make(map[string]interface{})
		if len(data.ReportRecipients.UserGroups) > 0 {
			var user_groupsList []map[string]interface{}
			for _, listItem := range data.ReportRecipients.UserGroups {
				listItemMap := make(map[string]interface{})
				if !listItem.Name.IsNull() && !listItem.Name.IsUnknown() {
					listItemMap["name"] = listItem.Name.ValueString()
				}
				if !listItem.Namespace.IsNull() && !listItem.Namespace.IsUnknown() {
					listItemMap["namespace"] = listItem.Namespace.ValueString()
				}
				if !listItem.Tenant.IsNull() && !listItem.Tenant.IsUnknown() {
					listItemMap["tenant"] = listItem.Tenant.ValueString()
				}
				user_groupsList = append(user_groupsList, listItemMap)
			}
			report_recipientsMap["user_groups"] = user_groupsList
		}
		apiResource.Spec["report_recipients"] = report_recipientsMap
	}
	if data.Waap != nil {
		waapMap := make(map[string]interface{})
		if data.Waap.CurrentNamespace != nil {
			waapMap["current_namespace"] = map[string]interface{}{}
		}
		if data.Waap.Daily != nil {
			dailyNestedMap := make(map[string]interface{})
			if !data.Waap.Daily.ReportGenerationTime.IsNull() && !data.Waap.Daily.ReportGenerationTime.IsUnknown() {
				dailyNestedMap["report_generation_time"] = data.Waap.Daily.ReportGenerationTime.ValueString()
			}
			waapMap["daily"] = dailyNestedMap
		}
		if data.Waap.Monthly != nil {
			monthlyNestedMap := make(map[string]interface{})
			if !data.Waap.Monthly.Date.IsNull() && !data.Waap.Monthly.Date.IsUnknown() {
				monthlyNestedMap["date"] = data.Waap.Monthly.Date.ValueString()
			}
			if !data.Waap.Monthly.ReportGenerationTime.IsNull() && !data.Waap.Monthly.ReportGenerationTime.IsUnknown() {
				monthlyNestedMap["report_generation_time"] = data.Waap.Monthly.ReportGenerationTime.ValueString()
			}
			waapMap["monthly"] = monthlyNestedMap
		}
		if data.Waap.Namespaces != nil {
			namespacesNestedMap := make(map[string]interface{})
			waapMap["namespaces"] = namespacesNestedMap
		}
		if data.Waap.Weekly != nil {
			weeklyNestedMap := make(map[string]interface{})
			if !data.Waap.Weekly.Day.IsNull() && !data.Waap.Weekly.Day.IsUnknown() {
				weeklyNestedMap["day"] = data.Waap.Weekly.Day.ValueString()
			}
			if !data.Waap.Weekly.ReportGenerationTime.IsNull() && !data.Waap.Weekly.ReportGenerationTime.IsUnknown() {
				weeklyNestedMap["report_generation_time"] = data.Waap.Weekly.ReportGenerationTime.ValueString()
			}
			waapMap["weekly"] = weeklyNestedMap
		}
		apiResource.Spec["waap"] = waapMap
	}

	_, err := r.client.UpdateReportConfig(ctx, apiResource)
	if err != nil {
		resp.Diagnostics.AddError("Client Error", fmt.Sprintf("Unable to update ReportConfig: %s", err))
		return
	}

	// Use plan data for ID since API response may not include metadata.name
	data.ID = types.StringValue(data.Name.ValueString())

	// Fetch the resource to get complete state including computed fields
	// PUT responses may not include all computed nested fields (like tenant in Object Reference blocks)
	fetched, fetchErr := r.client.GetReportConfig(ctx, data.Namespace.ValueString(), data.Name.ValueString())
	if fetchErr != nil {
		resp.Diagnostics.AddError("Client Error", fmt.Sprintf("Unable to read ReportConfig after update: %s", fetchErr))
		return
	}

	// Set computed fields from API response

	// Unmarshal spec fields from fetched resource to Terraform state
	apiResource = fetched // Use GET response which includes all computed fields
	isImport := false     // Update is never an import
	_ = isImport          // May be unused if resource has no blocks needing import detection
	if blockData, ok := apiResource.Spec["report_recipients"].(map[string]interface{}); ok && (isImport || data.ReportRecipients != nil) {
		data.ReportRecipients = &ReportConfigReportRecipientsModel{
			UserGroups: func() []ReportConfigReportRecipientsUserGroupsModel {
				if listData, ok := blockData["user_groups"].([]interface{}); ok && len(listData) > 0 {
					var result []ReportConfigReportRecipientsUserGroupsModel
					for _, item := range listData {
						if itemMap, ok := item.(map[string]interface{}); ok {
							result = append(result, ReportConfigReportRecipientsUserGroupsModel{
								Name: func() types.String {
									if v, ok := itemMap["name"].(string); ok && v != "" {
										return types.StringValue(v)
									}
									return types.StringNull()
								}(),
								Namespace: func() types.String {
									if v, ok := itemMap["namespace"].(string); ok && v != "" {
										return types.StringValue(v)
									}
									return types.StringNull()
								}(),
								Tenant: func() types.String {
									if v, ok := itemMap["tenant"].(string); ok && v != "" {
										return types.StringValue(v)
									}
									return types.StringNull()
								}(),
							})
						}
					}
					return result
				}
				return nil
			}(),
		}
	}
	if _, ok := apiResource.Spec["waap"].(map[string]interface{}); ok && isImport && data.Waap == nil {
		// Import case: populate from API since state is nil and psd is empty
		data.Waap = &ReportConfigWaapModel{}
	}
	// Normal Read: preserve existing state value

	psd := privatestate.NewPrivateStateData()
	// Use UID from fetched resource
	uid := fetched.Metadata.UID
	psd.SetUID(uid)
	psd.SetCustom("managed", "true") // Preserve managed marker after Update
	resp.Diagnostics.Append(psd.SaveToPrivateState(ctx, resp)...)

	resp.Diagnostics.Append(resp.State.Set(ctx, &data)...)
}

func (r *ReportConfigResource) Delete(ctx context.Context, req resource.DeleteRequest, resp *resource.DeleteResponse) {
	var data ReportConfigResourceModel
	resp.Diagnostics.Append(req.State.Get(ctx, &data)...)
	if resp.Diagnostics.HasError() {
		return
	}

	deleteTimeout, diags := data.Timeouts.Delete(ctx, inttimeouts.DefaultDelete)
	resp.Diagnostics.Append(diags...)
	if resp.Diagnostics.HasError() {
		return
	}

	ctx, cancel := context.WithTimeout(ctx, deleteTimeout)
	defer cancel()
	err := r.client.DeleteReportConfig(ctx, data.Namespace.ValueString(), data.Name.ValueString())
	if err != nil {
		// If the resource is already gone, consider deletion successful (idempotent delete)
		if strings.Contains(err.Error(), "NOT_FOUND") || strings.Contains(err.Error(), "404") {
			tflog.Warn(ctx, "ReportConfig already deleted, removing from state", map[string]interface{}{
				"name":      data.Name.ValueString(),
				"namespace": data.Namespace.ValueString(),
			})
			return
		}
		// If delete is not implemented (501), warn and remove from state
		// Some F5 XC resources don't support deletion via API
		if strings.Contains(err.Error(), "501") {
			tflog.Warn(ctx, "ReportConfig delete not supported by API (501), removing from state only", map[string]interface{}{
				"name":      data.Name.ValueString(),
				"namespace": data.Namespace.ValueString(),
			})
			return
		}
		resp.Diagnostics.AddError("Client Error", fmt.Sprintf("Unable to delete ReportConfig: %s", err))
		return
	}
}

func (r *ReportConfigResource) ImportState(ctx context.Context, req resource.ImportStateRequest, resp *resource.ImportStateResponse) {
	// Import ID format: namespace/name
	parts := strings.Split(req.ID, "/")
	if len(parts) != 2 || parts[0] == "" || parts[1] == "" {
		resp.Diagnostics.AddError(
			"Invalid Import ID",
			fmt.Sprintf("Expected import ID format: namespace/name, got: %s", req.ID),
		)
		return
	}
	namespace := parts[0]
	name := parts[1]

	resp.Diagnostics.Append(resp.State.SetAttribute(ctx, path.Root("namespace"), namespace)...)
	resp.Diagnostics.Append(resp.State.SetAttribute(ctx, path.Root("name"), name)...)
	resp.Diagnostics.Append(resp.State.SetAttribute(ctx, path.Root("id"), name)...)
}
